import { Component , Input} from '@angular/core';
import { ThemeService } from 'src/app/theme.service';
@Component({
  selector: 'app-vision-card',
  templateUrl: './vision-card.component.html',
  styleUrls: ['./vision-card.component.scss']
})
export class VisionCardComponent {
    @Input() cardData : any;

    currentTheme: string = 'light';
    constructor(private themeService: ThemeService){}

    ngOnInit(): void {
      this.themeService.currentTheme$.subscribe(theme => {
        this.currentTheme = theme;
      });
    }
}
<div class="card mb-2" [ngClass]="currentTheme === 'light' ? 'light-mode' : 'dark-mode'">
    <img [src]="cardData.imageUrl" class="card-img-top" alt="Wild Landscape"/>
    <div class="card-body">
      <h5 class="card-title" [ngClass]="currentTheme === 'light' ? 'light-mode' : 'dark-mode'">{{ cardData.title }}</h5>
    </div>
  </div>
