import { Component } from '@angular/core';

@Component({
  selector: 'app-blogs',
  templateUrl: './blogs.component.html',
  styleUrls: ['./blogs.component.scss']
})
export class BlogsComponent {

  blogs = [
    {imageUrl: '../assets/images/solutions-architect.png', title: 'Card title 1', content: 'Some quick example text for card 1'},
    {imageUrl: '../assets/images/aws2.png', title: 'Card title 2', content: 'Some quick example text for card 2'}
  ];

}
