import { Component } from '@angular/core';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.scss']
})
export class ProjectsComponent {
  blogs = [
    {imageUrl: '../assets/images/solutions-architect.png', title: 'Card title 1', content: 'Some quick example text for card 1'},
    {imageUrl: '../assets/images/aws2.png', title: 'Card title 2', content: 'Some quick example text for card 2'},
    {imageUrl: '../assets/images/hacker-rank.png', title: 'Card title 2', content: 'Some quick example text for card 2'},
    {imageUrl: '../assets/images/gfg1.jpg',  title: 'Card title 2', content: 'Some quick example text for card 2'}
  ];
}
